
public class STR1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="abcdcba";
		String S="";
	      char ch;
	      for(int i=str.length()-1;i>0;i--){
	   ch=str.charAt(i);
	  S=S+ch;	
	}
	      if(S.equals(str)){
	        System.out.println(true);
	      }
	      else
	    	  System.out.println(true);
		}
	

	}


